import{_ as a,o as e,c as t,d as h}from"./app.5ae1f617.js";const i={},r=h('<h1 id="\u5F00\u653E\u6388\u6743-oauth2-0" tabindex="-1"><a class="header-anchor" href="#\u5F00\u653E\u6388\u6743-oauth2-0" aria-hidden="true">#</a> \u5F00\u653E\u6388\u6743 OAuth2.0</h1><p>\u5F00\u653E\u6388\u6743\u6307\u6388\u6743\u7B2C\u4E09\u65B9\u8BBF\u95EE\u6211\u4EEC\u7684\u8D44\u6E90. \u6BD4\u5982\u4F7F\u7528\u5FAE\u4FE1\u767B\u5F55, \u53EF\u4EE5\u83B7\u5F97\u5FAE\u4FE1\u7528\u6237\u7684\u6635\u79F0\u548C\u624B\u673A\u53F7\u7801.</p><p>\u4E86\u89E3oauth\u4E4B\u524D\u6211\u4EEC\u5148\u4E86\u89E3\u4EC0\u4E48\u53EB\u505A\u6388\u6743.</p><p>\u6388\u6743\u901A\u5E38\u662F\u6307\u4E24\u4E2A\u89D2\u8272\u4E4B\u95F4\u7684\u6743\u529B\u59D4\u6258\u5173\u7CFB, \u66F4\u5177\u4F53\u7684\u5C31\u662F\u6709\u6743\u4E00\u65B9\u5C06\u6743\u529B\u6388\u4E88\u65E0\u6743\u4E00\u65B9.</p><p>\u6BD4\u5982\u4E0B\u9762\u51E0\u79CD\u5178\u578B\u7684\u6388\u6743:</p><ul><li>\u79DF\u8D41\u5173\u7CFB</li><li>\u501F\u8D37\u5173\u7CFB</li><li>\u7F51\u7AD9\u767B\u5F55\u9274\u6743</li></ul><p>\u5F00\u653E\u6388\u6743\u548C\u6388\u6743\u4E0D\u540C\u7684\u5730\u65B9\u5728\u4E8E, \u53D1\u751F\u4E3B\u4F53\u4E0D\u6B62\u4E24\u4E2A, \u9664\u4E86\u6388\u6743(\u7528\u6237)\u548C\u88AB\u6388\u6743(\u7B2C\u4E09\u65B9\u7528\u6237)\u7684\u4E24\u4E2A\u89D2\u8272\u4E3B\u4F53\u5916, \u8FD8\u6709\u4E2A\u5B58\u50A8\u4F60\u8D44\u6E90\u7684\u89D2\u8272\u4E3B\u4F53. \u6B63\u662F\u56E0\u4E3A\u4F60\u7684\u8D44\u6E90\u5E76\u6CA1\u6709\u5728\u4F60\u8EAB\u8FB9, \u4F60\u65E0\u6CD5\u76F4\u63A5\u628A\u4F60\u7684\u8D44\u6E90\u7ED9\u5230\u8D44\u6E90\u50A8\u5B58\u4E3B\u4F53, \u4F60\u9700\u8981\u901A\u77E5\u8D44\u6E90\u50A8\u5B58\u4E3B\u4F53\u6388\u6743, \u8FD9\u4E00\u8FC7\u7A0B\u5C31\u53EB\u5F00\u653E\u6388\u6743.</p><h2 id="\u89D2\u8272" tabindex="-1"><a class="header-anchor" href="#\u89D2\u8272" aria-hidden="true">#</a> \u89D2\u8272</h2><p>OAuth\u7684\u6D41\u7A0B\u4E2D\uFF0C\u4E3B\u8981\u6709\u5982\u4E0B\u56DB\u4E2A\u89D2\u8272:</p><ul><li>\u8D44\u6E90\u62E5\u6709\u8005(\u7528\u6237)</li><li>\u8D44\u6E90\u670D\u52A1\u5668(\u8D44\u6E90\u4FDD\u5B58\u7684\u5730\u65B9)</li><li>\u6388\u6743\u670D\u52A1\u5668(\u5E2E\u5FD9\u751F\u6210\u4EE4\u724C\u8FDB\u884C\u6388\u6743\u7684\u5730\u65B9, \u548C\u8D44\u6E90\u670D\u52A1\u5668\u540C\u5C5E\u4E00\u4E2A\u4E3B\u4F53)</li><li>\u60F3\u8981\u8BBF\u95EE\u7528\u6237\u7684\u5BA2\u6237\u7AEF(\u7B2C\u4E09\u65B9\u5E94\u7528\u6216\u8005\u7F51\u7AD9)</li></ul><h2 id="\u7B2C\u4E09\u65B9\u7F51\u7AD9\u4F7F\u7528\u5FAE\u4FE1\u767B\u5F55\u7684oauth2\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u65B9\u7F51\u7AD9\u4F7F\u7528\u5FAE\u4FE1\u767B\u5F55\u7684oauth2\u6D41\u7A0B" aria-hidden="true">#</a> \u7B2C\u4E09\u65B9\u7F51\u7AD9\u4F7F\u7528\u5FAE\u4FE1\u767B\u5F55\u7684OAuth2\u6D41\u7A0B</h2><p>\u7528\u6237\u70B9\u51FB\u4F7F\u7528\u5FAE\u4FE1\u767B\u5F55 -&gt; \u8DF3\u51FA\u5FAE\u4FE1\u626B\u7801\u9875\u9762(\u6216\u8005\u624B\u673A\u4E0A\u76F4\u63A5\u8DF3\u8F6C\u5230\u5FAE\u4FE1\u767B\u5F55\u786E\u8BA4\u9875) -&gt; \u83B7\u5F97\u6388\u6743(token\u4E4B\u7C7B\u7684\u4EE4\u724C, \u901A\u8FC7\u8FD9\u4E2A\u4EE4\u724C\u8BBF\u95EE\u8D44\u6E90)\u5E76\u56DE\u5230\u7B2C\u4E09\u65B9\u7F51\u7AD9</p><h2 id="\u4EE4\u724C" tabindex="-1"><a class="header-anchor" href="#\u4EE4\u724C" aria-hidden="true">#</a> \u4EE4\u724C</h2><p>\u4EE4\u724C\u662F\u8BBF\u95EE\u7528\u6237\u8D44\u6E90\u7684\u4E00\u4E2A\u901A\u884C\u8BC1, \u7531\u6388\u6743\u670D\u52A1\u5668\u6388\u6743\u540E\u83B7\u5F97, \u4EE4\u724C\u7684\u6548\u679C\u662F\u6709\u9650\u7684, \u5305\u62EC\u8FC7\u671F\u65F6\u95F4, \u6709\u9650\u7684\u8BBF\u95EE\u6743\u9650(\u6BD4\u5982\u5FAE\u4FE1\u53EA\u80FD\u8BBF\u95EE\u6635\u79F0\u548C\u624B\u673A, \u4E0D\u80FD\u591F\u8BBF\u95EE\u901A\u8BAF\u5F55, \u804A\u5929\u8BB0\u5F55\u7B49\u7B49).</p><p>\u4ECE\u7B2C\u4E09\u65B9\u5E94\u7528\u7684\u89C6\u89D2\u6765\u8BB2, \u6388\u6743\u7684\u8FC7\u7A0B\u7B49\u540C\u4E8E\u8D26\u53F7\u5BC6\u7801\u767B\u5F55\u7684\u8FC7\u7A0B, \u5728\u5927\u591A\u6570web\u5E94\u7528\u4E2D\u90FD\u662F\u57FA\u4E8Etoken\u548Csession\u6765\u8FDB\u884C\u6743\u9650\u6821\u9A8C\u7684, \u4E5F\u6709\u8FC7\u671F\u65F6\u95F4, \u4E5F\u6709\u6743\u9650\u8303\u56F4, \u4E0D\u540C\u7684\u53EA\u662F\u4E00\u4E2A\u662F\u4ECE\u81EA\u5BB6\u670D\u52A1\u5668\u4E0A\u62FF\u5230\u7684, \u4E00\u4E2A\u662F\u4ECE\u522B\u4EBA\u90A3\u8FB9\u62FF\u5230\u7684.</p>',15),l=[r];function o(d,n){return e(),t("div",null,l)}var s=a(i,[["render",o],["__file","oauth2.html.vue"]]);export{s as default};
